  //not a grill status messasge
  return null;
}

const parts = parseHexMessage(message);
if (parts < 36) {
  return null;
}
const status = {
 // p1Target: convertTemperature(parts, 2),
  p1Temp: convertTemperature(parts, 5),
  p2Temp: convertTemperature(parts, 8),
  p3Temp: convertTemperature(parts, 11),
  moduleIsOn: parts[18] === 1,
  err1: parts[19] === 1,
  err2: parts[20] === 1,
  err3: parts[21] === 1,
  highTempErr: parts[22] === 1,
  fanErr: parts[23] === 1,
  hotErr: parts[24] === 1,
  motorErr: parts[25] === 1,
  noPellets: parts[26] === 1,
  fanState: parts[27] === 1,
  hotState: parts[28] === 1,
  motorState: parts[29] === 1,
  lightState: parts[30] === 1,
  isFahrenheit: parts[31] === 1,
  recipeStep: parts[32],
  recipeTime: parts[33] * 3600 + parts[34] * 60 + parts[35],
};
switch (parts[17]) {
  case 1:
    status.grillSetTemp = convertTemperature(parts, 14);
    break;
  case 2:
    status.grillTemp = convertTemperature(parts, 14);
    break;
}

if (!status.isFahrenheit) {
  const ftoc = function (temp) {
    if ((typeof temp === "undefined") || (temp === 960)) {
      return temp;
    }
    return Math.floor((temp - 32) / 1.8);
  };
  status.p1Temp = ftoc(status.p1Temp);
  status.p2Temp = ftoc(status.p2Temp);
  status.p3Temp = ftoc(status.p3Temp);
  status.grillSetTemp = ftoc(status.grillSetTemp);
  status.grillTemp = ftoc(status.grillTemp);
}

return status;

